mapper.insert:

	SELECT NVL(MAX(BOARD_NUM), 0) + 1 FROM BOARD

INSERT INTO BOARD(BOARD_NUM
,BOARD_TITLE
,MEM_NUM
,BOARD_CONTENTS
,BOARD_DATE
,BOARD_HITS
)
VALUES(#{board_num}
, #{board_title}
, #{mem_num}
, #{board_contents}
, SYSDATE
, 0
)

mapper.update:
UPDATE BOARD 
SET
    BOARD_TITLE = #{board_title}, 
    BOARD_CONTENTS = #{board_contents}	 
WHERE BOARD_NUM = #{board_num}

